contracts/AuctionManage.sol.content:
// SPDX-License-Identifier: GPL-3.0-only

pragma solidity ^0.8.9;

import {IERC721, IERC165} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {ERC721Holder} from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import {IERC1155} from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import {ERC1155Holder} from "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import {ReentrancyGuard} from "./libraries/ReentrancyGuard.sol";
import {AssetType, Auction} from "./libraries/Structs.sol";

/**
 * @title English Auction Manager Contract
 */
contract AuctionManager is ReentrancyGuard, ERC721Holder, ERC1155Holder, Ownable, UUPSUpgradeable {
  using SafeERC20 for IERC20;

  /// @dev acutions
  mapping(bytes32 => Auction) public auctions;

  /// @dev emit this event when auction started
  event AuctionStarted(
    bytes32 indexed id,
    address indexed collection,
    address paymentToken,
    uint256 tokenId,
    uint256 minPrice,
    uint256 startTime,
    uint256 endTime
  );
  /// @dev emit this event when new bid added
  event NewBid(bytes32 indexed id, address indexed bidder, uint256 indexed bidPrice);
  /// @dev emit this event when auction finished
  event Finished(bytes32 indexed id, address indexed winner, uint256 indexed bidPrice);

  constructor() {}

  /* Constructor (for ERC1967) */
  function initialize() public initializer {}

  // required by the OZ UUPS module
  function _authorizeUpgrade(address) internal override onlyOwner {}

  /**
   * @notice create auction
   * @param _collection address of collection
   * @param _tokenId token id of nft
   * @param _paymentToken address of bid token
   * @param _minPrice minimum price of bidF
   * @param _duration duration of auction
   */
  function createAuction(
    address _collection,
    uint256 _tokenId,
    address _paymentToken,
    uint256 _minPrice,
    uint256 _duration
  ) external {
    AssetType assetType;
    if (IERC165(_collection).supportsInterface(type(IERC721).interfaceId)) {
      assetType = AssetType.ERC721;
    } else if (IERC165(_collection).supportsInterface(type(IERC1155).interfaceId)) {
      assetType = AssetType.ERC1155;
    } else {
      revert("invalid collection address");
    }

    // calculate id of auction
    bytes32 id = _calculateHash(_collection, _tokenId, _paymentToken, _minPrice);

    auctions[id] = Auction(
      assetType,
      _collection,
      _tokenId,
      _paymentToken,
      _minPrice,
      address(0),
      0,
      block.timestamp,
      block.timestamp + _duration,
      msg.sender
    );

    // lock asset to auction contract
    if (assetType == AssetType.ERC721) {
      IERC721(_collection).safeTransferFrom(msg.sender, address(this), _tokenId);
    } else {
      IERC1155(_collection).safeTransferFrom(msg.sender, address(this), _tokenId, 1, "");
    }

    emit AuctionStarted(
      id,
      _collection,
      _paymentToken,
      _tokenId,
      _minPrice,
      block.timestamp,
      block.timestamp + _duration
    );
  }

  /**
   * @notice make a new bid
   * @dev This functions changes last bid params, and release last bidder's token
   *      and locks new bidder's token
   * @param _id id of auction
   * @param _price new bidding price
   */
  function bid(bytes32 _id, uint256 _price) external payable nonReentrant {
    require(block.timestamp < auctions[_id].endTime, "Auction: This auction already finished");
    require(_price >= auctions[_id].minPrice, "Auction: bid price is low than minimum price");
    require(_price > auctions[_id].bidPrice, "Auction: bid price is low than last one");

    Auction memory auction = auctions[_id];

    address previousBidder = auction.lastBidder;
    uint256 previousPrice = auction.bidPrice;

    auction.lastBidder = msg.sender;
    auction.bidPrice = _price;
    auctions[_id] = auction;

    if (previousBidder != address(0)) {
      // release last bidder's token
      if (auction.paymentToken == address(0)) {
        payable(previousBidder).transfer(previousPrice);
      }
      IERC20(auction.paymentToken).safeTransfer(previousBidder, previousPrice);
    }

    // lock new bidder's token
    if (auction.paymentToken == address(0)) {
      require(_price == msg.value, "Auction: invalid eth amount");
    } else {
      IERC20(auction.paymentToken).safeTransferFrom(msg.sender, address(this), _price);
    }

    emit NewBid(_id, msg.sender, _price);
  }

  /**
   * @notice finish auction
   * @dev This function finishs auction.
   *      Sends asset to winner and sends bid token to owner.
   * @param _id id of auction
   */
  function finish(bytes32 _id) external {
    require(block.timestamp > auctions[_id].endTime, "Auction: auction not finished");
    require(
      auctions[_id].owner == msg.sender || auctions[_id].lastBidder == msg.sender,
      "Auction: don't have permission to finish"
    );

    // get asset receiver
    address assetReceiver;
    if (auctions[_id].lastBidder == address(0)) {
      assetReceiver = owner();
    } else {
      assetReceiver = auctions[_id].lastBidder;
    }

    // sends asset to receiver
    if (auctions[_id].assetType == AssetType.ERC721) {
      IERC721(auctions[_id].collection).safeTransferFrom(
        address(this),
        assetReceiver,
        auctions[_id].tokenId
      );
    } else if (auctions[_id].assetType == AssetType.ERC1155) {
      IERC1155(auctions[_id].collection).safeTransferFrom(
        address(this),
        assetReceiver,
        auctions[_id].tokenId,
        1,
        ""
      );
    }

    // sends bid token to owner
    if (auctions[_id].paymentToken == address(0)) {
      payable(auctions[_id].owner).transfer(auctions[_id].bidPrice);
    } else {
      IERC20(auctions[_id].paymentToken).safeTransfer(auctions[_id].owner, auctions[_id].bidPrice);
    }

    emit Finished(_id, assetReceiver, auctions[_id].bidPrice);
  }

  function _calculateHash(
    address _collection,
    uint256 _tokenId,
    address _paymentToken,
    uint256 _minPrice
  ) private view returns (bytes32) {
    return
      keccak256(
        abi.encodePacked(msg.sender, _collection, _tokenId, _paymentToken, _minPrice, block.number)
      );
  }
}

contracts/Collection.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {SuperChiefERC1155} from "./libraries/SuperChiefERC1155.sol";

/**
 * @title SuperChief Maketplace NFT Collection
 * @dev use ERC1155URIStorage standard
 */
contract Collection is SuperChiefERC1155 {
  /// @dev current max token id
  uint256 public maxId;

  /// @dev new collection added
  event SuperChiefCollectionCreated(
    address indexed collection,
    string name,
    string symbol,
    string contractURI,
    address owner
  );

  /**
   * @dev sets contract params
   * @param _name name of collection
   * @param _symbol symbol of collection
   * @param _contractURI uri of contract,
   * @param _executionDelegate address of execution delegate
   */
  constructor(
    string memory _name,
    string memory _symbol,
    string memory _contractURI,
    address _executionDelegate
  ) SuperChiefERC1155(_name, _symbol, _contractURI, _executionDelegate) {
    emit SuperChiefCollectionCreated(address(this), name, symbol, contractURI, msg.sender);
  }

  /**
   * @notice mints token to address by amount with tokenUri
   * @param to owner of NFT
   * @param amount amount of NFT
   * @param tokenUri uri of NFT
   */
  function mint(address to, uint256 amount, string calldata tokenUri) external onlyOwner {
    maxId++;
    _mint(to, maxId, amount, "");
    _setURI(maxId, tokenUri);
  }
}

contracts/ExecutionDelegate.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import {IExecutionDelegate} from "./interfaces/IExecutionDelegate.sol";

/**
 * @title ExecutionDelegate
 * @dev Proxy contract to manage user token approvals
 */
contract ExecutionDelegate is IExecutionDelegate, Ownable {
  mapping(address => bool) public contracts;
  mapping(address => bool) public revokedApproval;

  modifier approvedContract() {
    require(contracts[msg.sender], "Contract is not approved to make transfers");
    _;
  }

  event ApproveContract(address indexed _contract);
  event DenyContract(address indexed _contract);

  event RevokeApproval(address indexed user);
  event GrantApproval(address indexed user);

  /**
   * @dev Approve contract to call transfer functions
   * @param _contract address of contract to approve
   */
  function approveContract(address _contract) external onlyOwner {
    contracts[_contract] = true;
    emit ApproveContract(_contract);
  }

  /**
   * @dev Revoke approval of contract to call transfer functions
   * @param _contract address of contract to revoke approval
   */
  function denyContract(address _contract) external onlyOwner {
    contracts[_contract] = false;
    emit DenyContract(_contract);
  }

  /**
   * @dev Block contract from making transfers on-behalf of a specific user
   */
  function revokeApproval() external {
    revokedApproval[msg.sender] = true;
    emit RevokeApproval(msg.sender);
  }

  /**
   * @dev Allow contract to make transfers on-behalf of a specific user
   */
  function grantApproval() external {
    revokedApproval[msg.sender] = false;
    emit GrantApproval(msg.sender);
  }

  /**
   * @dev Transfer ERC721 token using `transferFrom`
   * @param collection address of the collection
   * @param from address of the sender
   * @param to address of the recipient
   * @param tokenId tokenId
   */
  function transferERC721Unsafe(
    address collection,
    address from,
    address to,
    uint256 tokenId
  ) external approvedContract {
    require(revokedApproval[from] == false, "User has revoked approval");
    IERC721(collection).transferFrom(from, to, tokenId);
  }

  /**
   * @dev Transfer ERC721 token using `safeTransferFrom`
   * @param collection address of the collection
   * @param from address of the sender
   * @param to address of the recipient
   * @param tokenId tokenId
   */
  function transferERC721(
    address collection,
    address from,
    address to,
    uint256 tokenId
  ) external approvedContract {
    require(revokedApproval[from] == false, "User has revoked approval");
    IERC721(collection).safeTransferFrom(from, to, tokenId);
  }

  /**
   * @dev Transfer ERC1155 token using `safeTransferFrom`
   * @param collection address of the collection
   * @param from address of the sender
   * @param to address of the recipient
   * @param tokenId tokenId
   * @param amount amount
   */
  function transferERC1155(
    address collection,
    address from,
    address to,
    uint256 tokenId,
    uint256 amount
  ) external approvedContract {
    require(revokedApproval[from] == false, "User has revoked approval");
    IERC1155(collection).safeTransferFrom(from, to, tokenId, amount, "");
  }

  /**
   * @dev Transfer ERC20 token
   * @param token address of the token
   * @param from address of the sender
   * @param to address of the recipient
   * @param amount amount
   */
  function transferERC20(
    address token,
    address from,
    address to,
    uint256 amount
  ) external approvedContract returns (bool) {
    require(revokedApproval[from] == false, "User has revoked approval");
    return IERC20(token).transferFrom(from, to, amount);
  }
}

