contracts/libraries/Structs.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

enum Side {
  Buy,
  Sell
}

enum SignatureVersion {
  Single,
  Bulk
}

enum AssetType {
  ERC721,
  ERC1155
}

struct Fee {
  uint16 rate;
  address payable recipient;
}

struct Order {
  address trader;
  Side side;
  address matchingPolicy;
  address collection;
  uint256 tokenId;
  uint256 amount;
  address paymentToken;
  uint256 price;
  uint256 listingTime;
  /* Order expiration timestamp - 0 for oracle cancellations. */
  uint256 expirationTime;
  Fee[] fees;
  uint256 salt;
  bytes extraParams;
}

struct Input {
  Order order;
  uint8 v;
  bytes32 r;
  bytes32 s;
  bytes extraSignature;
  SignatureVersion signatureVersion;
  uint256 blockNumber;
}

struct Auction {
  AssetType assetType;
  address collection;
  uint256 tokenId;
  address paymentToken;
  uint256 minPrice;
  address lastBidder;
  uint256 bidPrice;
  uint256 startTime;
  uint256 endTime;
  uint256 amount;
  address owner;
  Fee[] fees;
}

struct Sig {
  bytes32 r;
  bytes32 s;
  uint8 v;
}
